INSERT INTO `blogs_article` VALUES (1,'درباره برق کشی ساختمان','مختصری در مورد انواع خدمات ما','','<p style=\"text-align: right;\">این مجموعه با هدف ارتقاء کیفیت برق ساختمان و صنایع و پایین بردن هزینه های انرژی (برق , گاز ..) مصرف انها&nbsp; و رضایت مشتریان شکل گرفته است .</p>\r\n<p style=\"text-align: right;\">اهداف و موضوعات کاری</p>\r\n<p style=\"text-align: right;\">ـ تأکید بر مشتری مداری</p>\r\n<p style=\"text-align: right;\">ـ گسترش رفاه ساکنین&nbsp; ساختمان</p>\r\n<p style=\"text-align: right;\">ـ توجه به اصول صحیح برق کشی</p>\r\n<p style=\"text-align: right;\">مراحل شکل&zwnj;گیری</p>\r\n<p style=\"text-align: right;\">ـ گسترده &zwnj;شدن موضوعات&zwnj;درباره ما</p>\r\n<p style=\"text-align: right;\">ـ رويکرد به سرمايه&zwnj;هاي فكري</p>\r\n<p style=\"text-align: right;\">ـ سطح&zwnj;بندی مخاطبان</p>\r\n<p style=\"text-align: right;\">ـ خودگردانی<img src=\"http://electric-wiring.ir/wp-content/images/electric/about.jpg\" alt=\"برق کاری برج مسکونی\" /></p>\r\n<p style=\"text-align: right;\">ـ اهتمام به کوچک&zwnj;سازی ساختار برق ساختمان</p>\r\n<p style=\"text-align: right;\">ـ رعایت استانداردهای نظام مهندسی</p>\r\n<p style=\"text-align: right;\">بخش های مؤسسه</p>\r\n<p style=\"text-align: right;\">1-برق ساختمان</p>\r\n<p style=\"text-align: right;\">2-ایفون تصویری</p>\r\n<p style=\"text-align: right;\">3-دزدگیر</p>\r\n<p style=\"text-align: right;\">4-هوشمند سازی</p>\r\n<p style=\"text-align: right;\">5-<a title=\"برق اضطراری\" href=\"http://www.electric-wiring.ir/%d8%b3%db%8c%d9%85-%da%a9%d8%b4%db%8c-%d8%a8%d8%b1%d9%82-%d8%a7%d8%b6%d8%b7%d8%b1%d8%a7%d8%b1%db%8c/\">برق اضطراری</a></p>\r\n<p style=\"text-align: right;\">6-نورپردازی</p>\r\n<p style=\"text-align: right;\">7-تامین برق</p>\r\n<p style=\"text-align: right;\">8-<a title=\"برق کشی ساختمان\" href=\"http://electric-wiring.ir//\">برق کشی ساختمان</a></p>\r\n<p style=\"text-align: right;\">9-دوربین مداربسته</p>\r\n<p style=\"text-align: right;\">10- <a title=\"تلفن سانترال پاناسونیک\" href=\"http://dnet.ir\">سانترال تلفن</a></p>\r\n<p style=\"text-align: right;\">11-تابلو برق</p>\r\n<p style=\"text-align: right;\">12-انتن مرکزی</p>\r\n<p style=\"text-align: right;\">13-<a title=\"سیم کشی ساختمان\" href=\"http://www.electric-wiring.ir\">سیم کشی ساختمان</a></p>','','2023-04-07 18:10:10.000000','2023-04-07 18:10:10.000000'),(2,'دانلود کتاب و فیلم اموزشی','','','<p style=\"text-align: right;\">کتاب آموزشی نقشه کشی برق و علائم به زبان انگلیسی</p>\r\n<p style=\"text-align: right;\">کتاب آموزشی سیم کشی عامه برق به زبان انگلیسی</p>\r\n<p style=\"text-align: right;\">کتاب آموزشی خود اموز برقکاری به زبان انگلیسی</p>\r\n<p style=\"text-align: right;\">**************************************************************</p>\r\n<p style=\"text-align: right;\">&nbsp;</p>\r\n<p style=\"text-align: right;\">ویدئوی اموزشی راه اسان سیمکشی خانه</p>\r\n<p style=\"text-align: right;\">ویدئوی اموزشی سیمکشی برق</p>\r\n<p style=\"text-align: right;\">&nbsp;</p>','','2023-04-07 21:12:39.000000','2023-04-07 21:12:39.000000'),(3,'تماس با ما','','','<p>moodham@gmail.com</p>','','2023-04-07 21:46:11.000000','2023-04-07 21:46:11.000000');
